void EXTI0_IRQHandler(void){
	HAL_GPIO_EXTI_IRQHandler(Button0_Pin); // Clear interrupt flags

	uint32_t now = HAL_GetTick();

	if ((now - last_button_press) >= DEBOUNCE_DELAY)
	{
		last_button_press = now;
		// Stop DMA safely
		__HAL_TIM_DISABLE_DMA(&htim2, TIM_DMA_CC1);
		HAL_DMA_Abort_IT(&hdma_tim2_ch1);

		// TODO: Disable DMA transfer and abort IT, then start DMA in IT mode with new LUT and re-enable transfer
		current_waveform = (current_waveform + 1) % NUM_WAVEFORMS;

		switch (current_waveform)
		{
			case 0:
				lcd_command(CLEAR);
				lcd_putstring("SINE WAVE");
				HAL_DMA_Start_IT(&hdma_tim2_ch1,
								 (uint32_t)Sin_LUT,
								 (uint32_t)&(TIM3->CCR3),
								 NS);
				break;

			case 1:
				lcd_command(CLEAR);
				lcd_putstring("SAWTOOTH WAVE");
				HAL_DMA_Start_IT(&hdma_tim2_ch1,
								 (uint32_t)Saw_LUT,
								 (uint32_t)&(TIM3->CCR3),
								 NS);
				break;

			case 2:
				lcd_command(CLEAR);
				lcd_putstring("TRIANGLE WAVE");
				HAL_DMA_Start_IT(&hdma_tim2_ch1,
								 (uint32_t)Triangle_LUT,
								 (uint32_t)&(TIM3->CCR3),
								 NS);
				break;

			case 3:
				lcd_command(CLEAR);
				lcd_putstring("PIANO WAVE");
				HAL_DMA_Start_IT(&hdma_tim2_ch1,
								 (uint32_t)Triangle_LUT,
								 (uint32_t)&(TIM3->CCR3),
								 1024);
		   break;


			case 4:
				lcd_command(CLEAR);
				lcd_putstring("DRUM WAVE");
				HAL_DMA_Start_IT(&hdma_tim2_ch1,
								 (uint32_t)Triangle_LUT,
								 (uint32_t)&(TIM3->CCR3),
								 1024);
					break;

			case 5:
				lcd_command(CLEAR);
				lcd_putstring("GUITAR WAVE");
				HAL_DMA_Start_IT(&hdma_tim2_ch1,
								 (uint32_t)Triangle_LUT,
								 (uint32_t)&(TIM3->CCR3),
								 1024);
					break;

			// Optional: placeholder for future waveforms (e.g. piano, drum, guitar)
			default:
				lcd_command(CLEAR);
				lcd_putstring("SINE WAVE");
				HAL_DMA_Start_IT(&hdma_tim2_ch1,
								 (uint32_t)Sin_LUT,
								 (uint32_t)&(TIM3->CCR3),
								 1024);
				break;
		}
		// Re-enable DMA
		__HAL_TIM_ENABLE_DMA(&htim2, TIM_DMA_CC1);

	}
}
